import React, {Component} from 'react';
import { gql, useQuery } from '@apollo/client';

const getAuthorsQuery = gql`
{
    authors{
        name
        id
    }
}
`
class AddBook extends Component {
    render(){
        return(
            <form id="add-book">

                <div className="field">
                    <label>Book Name:</label>
                    <input type="text"/>
                </div>

                <div className="field">
                    <label>Genre:</label>
                    <input type="text"/>
                </div>

                <div className="field">
                    <label>Author:</label>
                    <select>
                        <option>
                            Select Author
                        </option>
                    </select>
                </div>

                <button>+</button>

            </form>
        )
    }
}

export default AddBook;
