import React, {Component, useState} from 'react';
import { useQuery } from '@apollo/client';
import { getBooksQuery } from '../queries/queries';

import  BookDetails  from '../components/BookDetails';

function Books({onBookSelected}) {

    sendId = (args) => {
        this.props.setBookId(args);
    }
    const { loading, error, data } = useQuery(getBooksQuery);

    if (loading) return <span>Loading...</span>;
    if (error) return <span>Error :(</span>;
    return data.books.map(book => {
        return(
        <li key={book.id} onClick={this.props.setBookId(args)} >{book.name}</li>
        )
    });
}

class BookList extends Component {
    state = {
        bookId:null
    }
    setBookId = (args) => {
        this.setState({id:args})
    }
    render(){
        console.log(this.props.onBookSelected);
        return(
            <div>

                <ul id="book-list">
                    <Books bookId={this.state.bookId}/>
                </ul>
                <BookDetails />
            </div>
        )
    }
}

export default BookList;
