import React, {Component} from 'react';
import { useQuery, compose, useMutation } from '@apollo/client';

import {getAuthorsQuery, addBookMutation} from '../queries/queries';


function Authors() {
    const { loading, error, data } = useQuery(getAuthorsQuery);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;
    return data.authors.map(author => {
        return(
        <option key={author.id} value={author.id}>{author.name}</option>
        )
    });
}

function AddBookButton() {
    const [addBook, { data }] = useMutation(addBookMutation);
    return(
        <form id="add-book" onSubmit={e => {
            e.preventDefault();
            addBook({ name: { type: this.state.name }, genre: {type: this.state.genre}, authorId: {type: this.state.authorId} });
            input.value = '';
          }}>

        <div className="field">
            <label>Book Name:</label>
            <input type="text" onChange={(e)=> this.setState({name:e.target.value})}/>
        </div>

        <div className="field">
            <label>Genre:</label>
            <input type="text" onChange={(e)=> this.setState({genre:e.target.value})}/>
        </div>

        <div className="field">
            <label>Author:</label>
            <select onChange={(e)=> this.setState({authorId:e.target.value})}>
                <option>
                    Select Author
                </option>
                <Authors/>
            </select>
        </div>

        <button type="submit">+</button>

    </form>
    )
}

class AddBook extends Component {
    constructor(props){
        super(props);
        this.state = {
            name:"",
            genre:"",
            authorId:""
        }
    }

    submitForm(e) {
        e.preventDefault()

    }

    render(){
        return(
            <AddBookButton/>
        )
    }
}

export default AddBook;
