import { useQuery } from '@apollo/client';
import { getBooksQuery } from '../queries/queries';

import  BookDetails  from '../components/BookDetails';

const Books = (props) => {
    let id = props;
    const [itemID, setItemID] = new useState(id);

    useEffect(() => {
        console.log(id);
        setItemID(id)
    },[id])

    const { loading, error, data } = useQuery(getBooksQuery);

    if (loading) return <span>Loading...</span>;
    if (error) return <span>Error :(</span>;
    return data.books.map(book => {
        return(
        <li key={book.id} onClick={(e) => { props = {bookId:book.id}; console.log(props);}} >{book.name}</li>
        )
    });
}

class BookList extends Component {
    constructor(props){
        super(props);
        this.state = {
            selected:{
                id:null,
                func: this.changeEffect.bind(this),
                writable: true
            }
        }
    }

    changeEffect(){
        console.log("changeEffect");
    }

    render(){
        return(
            <div>

                <ul id="book-list" onClick={(e) => {console.log(this.state.selected.id)}}>
                    <Books bookId={this}/>
                </ul>
                <BookDetails bookId={this.state.selected}/>
            </div>
        )
    }
}

export default BookList;
